class GameTimer {
	defaultInterval = 6000;
	MainTimer = false;
	status = 'stop';
	contols = {};
	caption = null;
	game = null;
	constructor(GameClass) {
		this.caption = document.body.querySelector('div.timer__watchclock');
		const contorlButtons = document.body.querySelector('div.timer__control');
		contorlButtons.querySelectorAll('*[data-timer-action]').forEach(item => {
			this.contols[item.dataset.timerAction] = item;
			let _self = this;
			item.addEventListener('click', (event) => _self[item.dataset.timerAction].call(_self, event));
		});
		this.game = GameClass;
	}
	start() {
		this.contols['pause'].classList.remove('hidden');
		this.contols['start'].classList.add('hidden');

		this.MainTimer = setInterval(() => {
			if (this.game.vars["timer"] > 0) {
				this.caption.innerText = this.inttotime((this.game.vars["timer"] -= 100));
				if (this.game.vars["timer"] < 1100 && [1000, 500, 300, 200, 100].indexOf(this.game.vars["timer"]) !== -1) this.beep();
			} else {
				this.pause();
				this.forward();
				this.beep();
			}
		}, 50);
	}
	pause() {
		clearInterval(this.MainTimer);
		this.MainTimer = false;
		this.contols['pause'].classList.add('hidden');
		this.contols['start'].classList.remove('hidden');
	}
	reset(nextStage) {
		this.pause()
		this.game.vars["timer"] = this.defaultInterval;
		this.caption.innerText = this.inttotime((this.game.vars["timer"]));
		if (nextStage !== 0) this.game.nextStage();
	}
	forward() {
		this.reset(1);
	}
	undo(event) {
		if (event.target.classList.contains('disabled')) return false;
		this.game.gameActionUndo();
	}
	beep() {
		new Audio(
			"data:audio/wav;base64,"
		).play();
	}
	inttotime(t) {
		let m = Math.floor(t / 6000);
		let s = Math.floor((t % 6000) / 100);
		let ms = t % 100;
		return "0" + m + ":" + (s > 9 ? s : "0" + s) + "." + (ms > 9 ? ms : "0" + ms);
	}
}
